import { useState } from 'react';
import confetti from 'canvas-confetti';

export default function AnimatedConfetti() {
  const [isAnimating, setIsAnimating] = useState<boolean>(false);

  const handleExecute = (): void => {
    setIsAnimating(true);
    confetti({
      particleCount: 100,
      spread: 70,
      origin: { y: 0.6 },
      colors: ["#ff0000", "#00ff00", "#0000ff", "#ffff00", "#ff00ff"]
    });

    setTimeout(() => setIsAnimating(false), 300);
  };

  return (
    <>
      <div className={`transition-transform duration-300 ${isAnimating ? 'scale-95' : 'scale-100'}`}>
        <h2>Click the button to see the animation!</h2>
      </div>
      <button onClick={handleExecute} className="bg-black text-white px-4 py-2 rounded-md">
        Execute
      </button>
    </>
  );
}
